', 'help:user:data:fr', 'household');
INSERT INTO wysiwyg_document VALUES (22, '2014-11-24 14:57:28.928', 'admin', '2014-11-24 14:57:28.929', 'admin', '<h1>Mentions l&eacute;gales du calculateur CO2 m&eacute;nages de l&#39;AWAC</h1>

