INSERT INTO wysiwyg_document VALUES (30, '2014-11-24 14:57:37.563', 'admin', '2014-11-24 14:57:37.563', 'admin', '<h1>Mentions l&eacute;gales de l&#39;acc&egrave;s v&eacute;rificateur sur les calculateurs CO2 de l&#39;AWAC</h1>

